sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	if (newValue == 'true'){
		var hosting = g_form.getValue('hosting_type');
		var contributors = g_form.getValue('is_top_contributors_widget_required');
		if ((hosting == 'Cloud - Hub') || (hosting =='Cloud - Hub and Engage') || (hosting == 'On-Premises - Hub') || (hosting == 'On-Premises - Hub and Engage')){
			g_form.setDisplay('sharepoint_document_search_results_source_id_production',true);
			g_form.setDisplay('sharepoint_people_search_results_source_id_production',true);
			g_form.setDisplay('sharepoint_search_site_collection_url_production',true);
			g_form.setDisplay('sharepoint_site_collection_url_production',true);
			g_form.setDisplay('sharepoint_sites_search_results_source_id_production',true);
		}
		if (contributors == 'Yes'){
			g_form.setDisplay('yammer_client_id_cms',true);
			g_form.setDisplay('yammer_client_id_production',true);
			g_form.setDisplay('yammer_client_secret_cms',true);
			g_form.setDisplay('yammer_client_secret_production',true);
		}
	}
	else {
		g_form.setDisplay('sharepoint_document_search_results_source_id_production',false);
		g_form.setDisplay('sharepoint_people_search_results_source_id_production',false);
		g_form.setDisplay('sharepoint_search_site_collection_url_production',false);
		g_form.setDisplay('sharepoint_site_collection_url_production',false);
		g_form.setDisplay('sharepoint_sites_search_results_source_id_production',false);
		g_form.setDisplay('yammer_client_id_cms',false);
		g_form.setDisplay('yammer_client_id_production',false);
		g_form.setDisplay('yammer_client_secret_cms',false);
		g_form.setDisplay('yammer_client_secret_production',false);
	}

}
