sys_ui_action.script:
function commentsDialog() {
	//Get the values to pass into the dialog
	var short_text = 'Please add your rejection reason'; //g_form.getValue("short_description");
	var sys_id = g_form.getUniqueValue();
	//Initialize and open the Dialog Window
	var dialog = new GlideModal("x_brig3_client_con_task_comments_dialog"); //Render the dialog containing the UI Page 'task_comments_dialog'
	dialog.setTitle("Add Reject Comments"); //Set the dialog title
	dialog.setPreference("short_text", short_text); //Pass in short description for use in the dialog
	dialog.setPreference("sys_id", sys_id); //Pass in short description for use in the dialog
	dialog.setPreference("stage",'general');
	dialog.render(); //Open the dialog
}



