sys_ui_page.client_script:
function validateComments() {
   //Gets called if the 'OK' dialog button is clicked
   //Make sure dialog comments are not empty
   var comments = gel("dial_comments").value;
   comments = trim(comments);
   if (comments == "") {
      //If comments are empty stop submission
      alert("Please provide comments to submit the dialog.");
      return false;
   }
   //If comments are not empty do this...
return true;
}

sys_ui_page.processing_script:
var getClientConf = new GlideRecord('x_brig3_client_con_client_configuration');
getClientConf.get(current_sys_id);

gs.eventQueue('x_brig3_client_con.send_notification',getClientConf,getClientConf.sys_id,current_stage);

if (current_stage == 'general'){
	getClientConf.general_pre_requisites = false;
	getClientConf.success_general_submission_complete = false;
	getClientConf.u_general_rejection_message = dial_comments;
	getClientConf.general_pre_requisites_status = 'In progress with client';
}

if (current_stage == 'staging'){
	getClientConf.staging_stage_completed = false;
	getClientConf.u_success_staging_submission_complete = false;
	getClientConf.u_staging_rejection_message = dial_comments;
	getClientConf.staging_pre_requisites_status = 'In progress with client';

}
if (current_stage == 'production'){
	getClientConf.production_stage_completed = false;
	getClientConf.u_success_production_submission_complete = false;
	getClientConf.u_production_rejection_message = dial_comments;
	getClientConf.production_pre_requisites_status = 'In progress with client';
}

getClientConf.update();
var urlOnStack = "x_brig3_client_con_client_configuration.do?sys_id="+current_sys_id;
response.sendRedirect(urlOnStack);
